import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import { PersonService } from './service/personServic';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import 'primereact/resources/themes/nova/theme.css'
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";
import {Menubar} from 'primereact/menubar';
import { Dialog } from 'primereact/dialog';
import { Panel } from 'primereact/panel';
import { InputText } from 'primereact/inputtext';

export default class App extends Component {
  constructor() {
    super();
    this.state = {};
    this.items = [
      {
        label: 'Nuevo',
        icon: 'pi pi-fw pi-plus',
        command: () => {this.showSaveDialog()}
      },
      {
        label: 'Editar',
        icon: 'pi pi-fw pi-pencil',
        command: () => {this.showSaveDialog()}
      },
      {
        label: 'Eliminar',
        icon: 'pi pi-fw pi-trash',
        command: () => {this.showSaveDialog()}
      }
    ];
    this.personService = new PersonService();

  }
  componentDidMount() {
    this.personService.getAll().then(data => this.setState({ personas: data }))
    this.setState({
      visible : false,
      persona: {
        id: null,
        firstName: null,
        lastName: null,
        address: null,
        phone : null
      }
    });
  }


  render() {
    return (
      <div style={{ width: '80%', marginTop: '20%', margin: '0 auto' }}>
        <Menubar model={this.items} />


        <Panel header="React Java Applications" >
          <DataTable value={this.state.personas}>
            <Column field="personId" header="ID"></Column>
            <Column field="firstName" header="FirstName"></Column>
            <Column field="lastName" header="LastName"></Column>
            <Column field="address" header="Address"></Column>
            <Column field="phone" header="Phone"></Column>
          </DataTable>
        </Panel>

        <Dialog header="Create Person" visible={this.state.visible} style={{ width: '400px' }} footer={this.footer} modal={true} onHide={() => this.setState({ visible: false })}></Dialog>
        <span className="p-float-label">
          <InputText value={this.state.persona.firstName} style={{ width: '100%' }} id="firstName" onChange={(e) => {
            let val = e.target.value;
            this.setState(prevState => {
              let persona = Object.assign({}, prevState.persona);
              persona.firstName = val

              return { persona };
            })
          }
          } />
          <label htmlFor="firstName">LastName</label>
        </span>
      </div>
    );
  }
  showSaveDialog() {
    this.setState({
      visible: true,

    });
  }
}
